
  $(document).ready(function(){
   // contact us
    $('#ContactForm').submit(function(e){
      e.preventDefault();
      if (validateForm()) {
        var name = $('#name').val();
        var email = $('#email').val();
        var telephone = $('#telephone').val();
        var message = $('#message').val();
        $('#userName').text(name);
        $('#userMessage').text(message);
        $('#thankYouModal').modal('show');
        $('#ContactForm')[0].reset();
    
      }
    });
    $('#feedbackForm').submit(function(a){
      a.preventDefault();
      if (validateForm_1()) {
        var name_feedback = $('#username_modal').val();
        var email_feedback = $('#email_modal').val();
        var telephone_feedback = $('#telephone_modal').val();
        var message_feedback = $('#message_modal').val();
        $('#userName_1').text(name_feedback);
        $('#userMessage_1').text(name_feedback);
        $('#thankYouModalfeedback').modal('show');
        $('#feedbackForm')[0].reset();
      }
    });

    function validateForm() {
      var valid = true;
      $('.error-message').text('');

      // name
      var name = $('#name').val();
      if (!name) {
        $('#nameError').text('Please enter your name.').css("color","red");
        valid = false;
      }else if (!isValidName(name)) {
    $('#nameError').text('Please enter a valid name').css("color","red");;
    valid = false;
  }

      // email
      var email = $('#email').val();
      if (!email) {
        $('#emailError').text('Please enter your email address.').css("color","red");
        valid = false;
      }else if (!isValidEmail(email)) {
    $('#emailError').text('Please enter a valid email address.').css("color","red");;
    valid = false;
  }
      
      //telephone
      var telephone = $('#telephone').val();
      if (!telephone) {
        $('#telephoneError').text('Please enter your phone number').css("color","red");
        valid = false;
      }else if (!isValidTelephone(telephone)) {
     $('#telephoneError').text('Please enter a valid telephone number.').css("color","red");
     valid = false;
      }


      //message
      var message = $('#message').val();
      if (!message) {
        $('#messageError').text('Please enter your message.').css("color","red");
        valid = false;
      }

      return valid;
    }

  //feedback
  function validateForm_1() {
      var valid = true;
      $('.error-message').text('');
    
      // usernmame
      var name_feedback = $('#username_modal').val();
      if (!name_feedback) {
        $('#nameError_modal').text('Please enter your name.').css("color","red");
        valid = false;
      }else if (!isValidName(name_feedback)) {
    $('#nameError_modal').text('Please enter a valid email address.').css("color","red");;
    valid = false;
  }

// email_feedback
      var email_feedback = $('#email_modal').val();
      if (!email_feedback) {
        $('#emailError_modal').text('Please enter your email address.').css("color","red");
        valid = false;
      }else if (!isValidEmail(email_feedback)) {
    $('#emailError_modal').text('Please enter a valid email address.').css("color","red");;
    valid = false;
  }
      // telephone_feedback
      var telephone_feedback = $('#telephone_modal').val();
      if (!telephone_feedback) {
        $('#telephoneError_modal').text('Please enter your phone number').css("color","red");
        valid = false;
      }
      else if (!isValidTelephone(telephone_feedback)) {
     $('#telephoneError_modal').text('Please enter a valid telephone number.').css("color","red");
     valid = false;
      }

      // message_feedback
      var message_feedback = $('#message_modal').val();
      if (!message_feedback) {
        $('#messageError_modal').text('Please enter your message.').css("color","red");
        valid = false;
      }

      return valid;
    }

    function isValidEmail(email,email_feedback) {
  var emailRegex = /\S+@\S+\.\S+/;
  return emailRegex.test(email,email_feedback);
}
function isValidName(name,name_feedback) {
  var nameRegex = /^[A-Za-z ]{3,20}$/;
  return nameRegex.test(name,name_feedback);
}
function isValidTelephone(telephone,telephone_feedback) {
  var telephoneRegex = /^[0-9]+$/;
  return telephoneRegex.test(telephone,telephone_feedback);
}


  });

